        <div className="bg-white p-6 rounded-lg w-full max-w-md">
          <div className="flex justify-between items-center mb-4">
            <h3 className="text-lg font-medium">
              Edit {editMode.type === 'user' ? 'User' : editMode.type === 'class' ? 'Class' : 'Update'}
            </h3>
            <button
              onClick={() => setEditMode({ type: null, data: null })}
              className="text-gray-500 hover:text-gray-700"
            >
              ×
            </button>
          </div>

          {editMode.type === 'update' ? (
            <form onSubmit={async (e) => {
              e.preventDefault();
              try {
                await api.put(`/admin/updates/${editMode.data._id}`, {
                  title: editMode.data.title,
                  content: editMode.data.content
                });
                setEditMode({ type: null, data: null });
                fetchUpdates();
              } catch (error) {
                console.error('Error updating update:', error);
              }
            }} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Title</label>
                <input
                  type="text"
                  value={editMode.data.title}
                  onChange={(e) => setEditMode({
                    ...editMode,
                    data: { ...editMode.data, title: e.target.value }
                  })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Content</label>
                <textarea
                  value={editMode.data.content}
                  onChange={(e) => setEditMode({
                    ...editMode,
                    data: { ...editMode.data, content: e.target.value }
                  })}
                  rows={4}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setEditMode({ type: null, data: null })}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 rounded-md"
                >
                  Save Changes
                </button>
              </div>
            </form>
          ) : editMode.type === 'user' ? (
            <form onSubmit={handleEditUser} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Name</label>
                <input
                  type="text"
                  value={editMode.data.name}
                  onChange={(e) => setEditMode({
                    ...editMode,
                    data: { ...editMode.data, name: e.target.value }
                  })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Email</label>
                <input
                  type="email"
                  value={editMode.data.email}
                  onChange={(e) => setEditMode({
                    ...editMode,
                    data: { ...editMode.data, email: e.target.value }
                  })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Role</label>
                <select
                  value={editMode.data.role}
                  onChange={(e) => setEditMode({
                    ...editMode,
                    data: { ...editMode.data, role: e.target.value }
                  })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                >
                  <option value="teacher">Teacher</option>
                  <option value="cr">CR</option>
                  <option value="admin">Admin</option>
                </select>
              </div>
              <div className="flex justify-end space-x-3">
                <button
                  type="button"
                  onClick={() => setEditMode({ type: null, data: null })}
                  className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 rounded-md"
                >
                  Save Changes
                </button>
              </div>
            </form>
          ) : (
            <form onSubmit={handleEditClass} className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Name</label>
                <input
                  type="text"
                  value={editMode.data.name || ''}
                  onChange={(e) => setEditMode({
                    ...editMode,
                    data: { ...editMode.data, name: e.target.value }
                  })}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  required
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Section</label>
                <input
                  type="text"
                  value={editMode.data.section || ''}
