                      user.role === 'cr' && 
                      (!isCRAssigned(user._id) || user._id === editMode.data.cr || 
                       (editMode.data.cr?._id && user._id === editMode.data.cr._id))
                    )
                    .map(cr => (
                      <option key={cr._id} value={cr._id}>
                        {cr.name} {cr._id === editMode.data.cr ? '(Current CR)' : ''}
