          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Teachers</label>
            <select
              multiple
              value={selectedTeachers}
              onChange={(e) => {
                const values = Array.from(e.target.selectedOptions, option => option.value);
                setSelectedTeachers(values);
