import { useState, useEffect } from 'react';
import DashboardLayout from '../../components/layout/DashboardLayout';
import api from '../../utils/api';

export default function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('users');
  const [users, setUsers] = useState([]);
  const [classes, setClasses] = useState([]);
  const [updates, setUpdates] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [editingClass, setEditingClass] = useState(null);
  const [selectedClass, setSelectedClass] = useState(null);
  const [showStudentModal, setShowStudentModal] = useState(false);
  const [classStudents, setClassStudents] = useState([]);
  const [editMode, setEditMode] = useState({
    type: null,
    data: null
  });

  // Form states
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    password: '',
    role: 'teacher',
  });

  const [newClass, setNewClass] = useState({
    branch: 'btech',
    course: 'cse',
    section: '',
    year: '',
    cr: '',
    classIncharge: '',
    teachers: []
  });

  const [newUpdate, setNewUpdate] = useState({
    title: '',
    content: '',
    isGlobal: true,
  });

  const [newStudent, setNewStudent] = useState({
    name: '',
    email: '',
    enrollmentNumber: '',
    password: '',
    role: 'student',
    classId: ''
  });

  // Add state for custom branch and course
  const [showCustomBranch, setShowCustomBranch] = useState(false);
  const [showCustomCourse, setShowCustomCourse] = useState(false);
  const [customBranch, setCustomBranch] = useState('');
  const [customCourse, setCustomCourse] = useState('');

  useEffect(() => {
    fetchUsers();
    fetchClasses();
    fetchUpdates();
    fetchAvailableStudents();
  }, []);

  // Fetch functions
  const fetchUsers = async () => {
    try {
      const response = await api.get('/admin/users');
      setUsers(response.data);
    } catch (error) {
      console.error('Error fetching users:', error.response?.data?.message || error.message);
    }
  };

  const fetchClasses = async () => {
    try {
      const response = await api.get('/admin/classes');
      // Transform the data to ensure CR is properly structured
      const transformedClasses = response.data.map(cls => ({
        ...cls,
        cr: cls.cr?._id || cls.cr // Handle both populated and unpopulated cases
      }));
      setClasses(transformedClasses);
    } catch (error) {
      console.error('Error fetching classes:', error.response?.data?.message || error.message);
    }
  };

  const fetchUpdates = async () => {
    try {
      const response = await api.get('/admin/updates');
      setUpdates(response.data);
    } catch (error) {
      console.error('Error fetching updates:', error.response?.data?.message || error.message);
    }
  };

  const fetchAvailableStudents = async () => {
    try {
      const response = await api.get('/admin/users?role=student&unassigned=true');
      setAvailableStudents(response.data);
    } catch (error) {
      console.error('Error fetching available students:', error);
    }
  };

  // Create functions
  const handleCreateUser = async (e) => {
    e.preventDefault();
    try {
      await api.post('/admin/users', newUser);
      setNewUser({
        name: '',
        email: '',
        password: '',
        role: 'teacher',
      });
      fetchUsers();
    } catch (error) {
      console.error('Error creating user:', error.response?.data?.message || error.message);
    }
  };

  const handleCreateClass = async (e) => {
    e.preventDefault();
    try {
      const className = `${newClass.branch.toUpperCase()} ${newClass.course.toUpperCase()} ${newClass.section}-${newClass.year} yr`;

      await api.post('/admin/classes', {
        ...newClass,
        name: className
      });

      // Reset form
      setNewClass({
        branch: 'btech',
        course: 'cse',
        section: '',
        year: '',
        cr: '',
        classIncharge: '',
        teachers: []
      });

      fetchClasses();
      fetchUsers();
    } catch (error) {
      console.error('Error creating class:', error);
      alert('Error creating class: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleCreateUpdate = async (e) => {
    e.preventDefault();
    try {
      await api.post('/admin/updates', newUpdate);
      setNewUpdate({
        title: '',
        content: '',
        isGlobal: true,
      });
      fetchUpdates();
    } catch (error) {
      console.error('Error creating update:', error.response?.data?.message || error.message);
    }
  };

  const handleAddStudent = (e) => {
    e.preventDefault();
    setNewClass(prev => ({
      ...prev,
      students: [...prev.students, { ...newStudent }]
    }));
    setNewStudent({
      name: '',
      email: '',
      enrollmentNumber: '',
      password: '',
      role: 'student',
      classId: ''
    });
  };

  const handleRemoveStudent = (index) => {
    setNewClass(prev => ({
      ...prev,
      students: prev.students.filter((_, i) => i !== index)
    }));
  };

  // Delete functions
  const handleDeleteUser = async (userId) => {
    try {
      await api.delete(`/admin/users/${userId}`);
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error.response?.data?.message || error.message);
    }
  };

  const handleDeleteClass = async (classId) => {
    try {
      await api.delete(`/admin/classes/${classId}`);
      fetchClasses();
    } catch (error) {
      console.error('Error deleting class:', error.response?.data?.message || error.message);
    }
  };

  const handleDeleteUpdate = async (updateId) => {
    try {
      await api.delete(`/admin/updates/${updateId}`);
      fetchUpdates();
    } catch (error) {
      console.error('Error deleting update:', error.response?.data?.message || error.message);
    }
  };

  // Edit functions
  const handleEditClick = (type, data) => {
    if (type === 'class') {
      // Transform the data to ensure all fields are properly set
      setEditingClass({
        ...data,
        branch: data.branch || 'btech',
        course: data.course || 'cse',
        section: data.section || '',
        year: data.year || '',
        classIncharge: data.classIncharge?._id || data.classIncharge || '',
        cr: data.cr?._id || data.cr || '',
        teachers: data.teachers?.map(t => typeof t === 'object' ? t._id : t) || []
      });
    } else {
      setEditingUser(data);
    }
    setEditMode({ type, data });
  };

  const handleEditClass = async (e) => {
    e.preventDefault();
    try {
      const { _id, ...classData } = editingClass;

      // Update the class
      await api.put(`/admin/classes/${_id}`, classData);

      setEditMode({ type: null, data: null });
      setEditingClass(null);
      await fetchClasses();
      await fetchUsers();

    } catch (error) {
      console.error('Error updating class:', error);
      alert('Error updating class: ' + (error.response?.data?.message || error.message));
    }
  };

  const fetchClassStudents = async (classId) => {
    try {
      const response = await api.get(`/admin/classes/${classId}/students`);
      // Transform the data if needed
      const transformedStudents = response.data.map(student => ({
        ...student,
        enrollmentNumber: student.enrollmentNumber || student.studentDetails?.enrollmentNumber
      }));
      setClassStudents(transformedStudents);
    } catch (error) {
      console.error('Error fetching class students:', error.response?.data?.message || error.message);
    }
  };

  const handleAddStudentToClass = async (classId, studentData) => {
    try {
      // Create new student with class assignment
      await api.post('/admin/users', {
        ...studentData,
        role: 'student',
        classId,
        studentDetails: {
          enrollmentNumber: studentData.enrollmentNumber,
          class: classId
        }
      });

      // Reset form
      setNewStudent({
        name: '',
        email: '',
        enrollmentNumber: '',
        password: '',
        role: 'student',
        classId: ''
      });

      // Refresh the students list for this class
      await fetchClassStudents(classId);
      await fetchUsers();

      // Show success message
      alert('Student added successfully!');
    } catch (error) {
      console.error('Error creating student:', error);
      alert('Error creating student: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleRemoveStudentFromClass = async (classId, studentId) => {
    try {
      await api.delete(`/admin/classes/${classId}/students/${studentId}`);
      await fetchClassStudents(classId);
    } catch (error) {
      console.error('Error removing student:', error);
    }
  };

  // Add this function
  const handleEditUser = async (e) => {
    e.preventDefault();
    try {
      const { _id, ...userData } = editingUser;
      await api.put(`/admin/users/${_id}`, userData);
      setEditMode({ type: null, data: null });
      setEditingUser(null);
      fetchUsers();
    } catch (error) {
      console.error('Error updating user:', error);
      alert('Error updating user: ' + (error.response?.data?.message || error.message));
    }
  };

  // Render functions
  const renderUserForm = () => (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-lg font-semibold mb-4">Create New User</h2>
      <form onSubmit={handleCreateUser} className="space-y-4">
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Name</label>
            <input
              type="text"
              value={newUser.name}
              onChange={(e) => setNewUser({ ...newUser, name: e.target.value })}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
              required
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700">Email</label>
            <input
              type="email"
              value={newUser.email}
