import React, { useState } from 'react';

export default function CustomDropdownWithAdd({ 
  type, 
  value, 
  onChange, 
  options, 
  onAddNew 
}) {
  const [showCustomInput, setShowCustomInput] = useState(false);
  const [customValue, setCustomValue] = useState('');

  const handleAddNew = () => {
    if (customValue.trim()) {
      onAddNew(customValue.trim().toLowerCase());
      setShowCustomInput(false);
      setCustomValue('');
    }
  };

  return (
    <div>
      {showCustomInput ? (
        <div className="mt-1 flex space-x-2">
          <input
            type="text"
            value={customValue}
            onChange={(e) => setCustomValue(e.target.value)}
            className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
            placeholder={`Enter new ${type}`}
            autoFocus
          />
          <button
            type="button"
            onClick={handleAddNew}
            className="px-3 py-1 text-sm text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
          >
            Add
          </button>
          <button
            type="button"
            onClick={() => {
              setShowCustomInput(false);
              setCustomValue('');
            }}
            className="px-3 py-1 text-sm text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
          >
            Cancel
          </button>
        </div>
      ) : (
        <select
          value={value}
          onChange={(e) => {
            if (e.target.value === 'add_new') {
              setShowCustomInput(true);
            } else {
              onChange(e.target.value);
            }
          }}
          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
          required
        >
          <option value="">Select {type}</option>
          {options.map(option => (
            <option key={option.value} value={option.value}>
              {option.label}
            </option>
          ))}
          <option value="add_new">+ Add New {type}</option>
        </select>
      )}
    </div>
  );
}
