import { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import DashboardLayout from '../../components/layout/DashboardLayout';

export default function StudentDashboard() {
  const { user } = useAuth();
  const [schedule, setSchedule] = useState(null);
  const [updates, setUpdates] = useState({
    cr: [],
    teacher: [],
    admin: []
  });
  const [notifications, setNotifications] = useState([]);
  const [featuredNews, setFeaturedNews] = useState([]);
  const [showFullSchedule, setShowFullSchedule] = useState(false);

  useEffect(() => {
    // Fetch today's schedule
    const fetchSchedule = async () => {
      try {
        const response = await fetch('/api/student/schedule', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setSchedule(data);
      } catch (error) {
        console.error('Error fetching schedule:', error);
      }
    };

    // Fetch updates
    const fetchUpdates = async () => {
      try {
        const response = await fetch('/api/student/updates', {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        });
        const data = await response.json();
        setUpdates(data);
      } catch (error) {
        console.error('Error fetching updates:', error);
      }
    };

    fetchSchedule();
    fetchUpdates();
  }, []);

  return (
    <DashboardLayout>
      <div className="space-y-6">
        {/* Schedule Section */}
        <div className="bg-white shadow rounded-lg p-4">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-lg font-semibold">Today's Schedule</h2>
            <button
              onClick={() => setShowFullSchedule(!showFullSchedule)}
              className="text-indigo-600 hover:text-indigo-800"
            >
              {showFullSchedule ? 'Show Today' : 'View Full Week'}
            </button>
          </div>
          {schedule && (
            <div className="space-y-2">
              {/* Render schedule items */}
              {schedule.map((item, index) => (
                <div key={index} className="flex justify-between p-2 hover:bg-gray-50">
                  <span>{item.subject}</span>
                  <span>{item.time}</span>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Updates Section */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* CR Updates */}
          <div className="bg-white shadow rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-4">Updates by CR</h3>
            <div className="space-y-2">
              {updates.cr.map((update, index) => (
                <div key={index} className="p-2 hover:bg-gray-50">
                  <p className="font-medium">{update.title}</p>
                  <p className="text-sm text-gray-600">{update.content}</p>
                  <span className="text-xs text-gray-500">{new Date(update.timestamp).toLocaleDateString()}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Teacher Updates */}
          <div className="bg-white shadow rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-4">Updates by Teacher</h3>
            <div className="space-y-2">
              {updates.teacher.map((update, index) => (
                <div key={index} className="p-2 hover:bg-gray-50">
                  <p className="font-medium">{update.title}</p>
                  <p className="text-sm text-gray-600">{update.content}</p>
                  <span className="text-xs text-gray-500">{new Date(update.timestamp).toLocaleDateString()}</span>
                </div>
              ))}
            </div>
          </div>

          {/* Admin Updates */}
          <div className="bg-white shadow rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-4">Updates by Admin</h3>
            <div className="space-y-2">
              {updates.admin.map((update, index) => (
                <div key={index} className="p-2 hover:bg-gray-50">
                  <p className="font-medium">{update.title}</p>
                  <p className="text-sm text-gray-600">{update.content}</p>
                  <span className="text-xs text-gray-500">{new Date(update.timestamp).toLocaleDateString()}</span>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Notifications and Featured News */}
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          {/* Notifications */}
          <div className="bg-white shadow rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-4">Notifications</h3>
            <div className="space-y-2">
              {notifications.map((notification, index) => (
                <div key={index} className="p-2 hover:bg-gray-50">
                  <p className="font-medium">{notification.title}</p>
                  <p className="text-sm text-gray-600">{notification.content}</p>
                  {notification.link && (
                    <a 
                      href={notification.link}
                      className="text-indigo-600 hover:text-indigo-800 text-sm"
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      Learn More
                    </a>
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Featured News */}
          <div className="bg-white shadow rounded-lg p-4">
            <h3 className="text-lg font-semibold mb-4">Featured News</h3>
            <div className="space-y-2">
              {featuredNews.map((news, index) => (
                <div key={index} className="p-2 hover:bg-gray-50">
                  <p className="font-medium">{news.title}</p>
                  <p className="text-sm text-gray-600">{news.content}</p>
                  <span className="text-xs text-gray-500">{new Date(news.timestamp).toLocaleDateString()}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </DashboardLayout>
  );
}
