                {/* Class Information Section */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="text-md font-medium text-gray-700 mb-4">Class Information</h4>
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700">Branch</label>
                      {showCustomBranch ? (
                        <div className="mt-1 flex space-x-2">
                          <input
                            type="text"
                            value={customBranch}
                            onChange={(e) => setCustomBranch(e.target.value.toLowerCase())}
                            className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                            placeholder="Enter new branch"
                          />
                          <button
                            type="button"
                            onClick={() => {
                              if (customBranch) {
                                setEditingClass({
                                  ...editingClass,
                                  branch: customBranch
                                });
                              }
                              setShowCustomBranch(false);
                              setCustomBranch('');
                            }}
                            className="px-3 py-1 text-sm text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
                          >
                            Add
                          </button>
                          <button
                            type="button"
                            onClick={() => {
                              setShowCustomBranch(false);
                              setCustomBranch('');
                            }}
                            className="px-3 py-1 text-sm text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
                          >
                            Cancel
                          </button>
                        </div>
                      ) : (
                        <div className="mt-1">
                          <select
                            value={editingClass?.branch || ''}
                            onChange={(e) => {
                              if (e.target.value === 'add_new') {
                                setShowCustomBranch(true);
                              } else {
                                setEditingClass({
                                  ...editingClass,
                                  branch: e.target.value
                                });
                              }
                            }}
                            className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                            required
                          >
                            <option value="">Select Branch</option>
                            <option value="btech">BTech</option>
                            <option value="mtech">MTech</option>
                            <option value="phd">PhD</option>
                            <option value="add_new">+ Add New Branch</option>
                          </select>
                        </div>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Course</label>
                      {showCustomCourse ? (
                        <div className="mt-1 flex space-x-2">
                          <input
                            type="text"
                            value={customCourse}
                            onChange={(e) => setCustomCourse(e.target.value.toLowerCase())}
                            className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                            placeholder="Enter new course"
                          />
                          <button
                            type="button"
                            onClick={() => {
                              if (customCourse) {
                                setEditingClass({
                                  ...editingClass,
                                  course: customCourse
                                });
                              }
                              setShowCustomCourse(false);
                              setCustomCourse('');
                            }}
                            className="px-3 py-1 text-sm text-white bg-indigo-600 rounded-md hover:bg-indigo-700"
                          >
                            Add
                          </button>
                          <button
                            type="button"
                            onClick={() => {
                              setShowCustomCourse(false);
                              setCustomCourse('');
                            }}
                            className="px-3 py-1 text-sm text-gray-600 bg-gray-100 rounded-md hover:bg-gray-200"
                          >
                            Cancel
                          </button>
                        </div>
                      ) : (
                        <div className="mt-1">
                          <select
                            value={editingClass?.course || ''}
                            onChange={(e) => {
                              if (e.target.value === 'add_new') {
                                setShowCustomCourse(true);
                              } else {
                                setEditingClass({
                                  ...editingClass,
                                  course: e.target.value
                                });
                              }
                            }}
                            className="block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                            required
                          >
                            <option value="">Select Course</option>
                            <option value="cse">CSE</option>
                            <option value="it">IT</option>
                            <option value="ece">ECE</option>
                            <option value="ee">EE</option>
                            <option value="add_new">+ Add New Course</option>
                          </select>
                        </div>
                      )}
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Section</label>
                      <input
                        type="text"
                        value={editingClass?.section || ''}
                        onChange={(e) => setEditingClass({
                          ...editingClass,
                          section: e.target.value.toUpperCase()
                        })}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                        placeholder="A"
                        required
                      />
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Year</label>
                      <input
                        type="number"
                        value={editingClass?.year || ''}
                        onChange={(e) => setEditingClass({
                          ...editingClass,
                          year: e.target.value
                        })}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                        min="1"
                        max="4"
                        required
                      />
                    </div>
                  </div>
                </div>

                {/* Staff Assignment Section */}
                <div className="bg-gray-50 p-4 rounded-lg">
                  <h4 className="text-md font-medium text-gray-700 mb-4">Staff Assignment</h4>
                  <div className="space-y-4">
                    <div>
                      <label className="block text-sm font-medium text-gray-700">Class In-charge</label>
                      <select
                        value={editingClass?.classIncharge || ''}
                        onChange={(e) => setEditingClass({
                          ...editingClass,
                          classIncharge: e.target.value
                        })}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                        required
                      >
                        <option value="">Select Class In-charge</option>
                        {users
                          ?.filter(user => user.role === 'teacher')
                          ?.map(teacher => (
                            <option key={teacher._id} value={teacher._id}>
                              {teacher.name}
                            </option>
                          ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">CR</label>
                      <select
                        value={editingClass?.cr?._id || editingClass?.cr || ''}
                        onChange={(e) => setEditingClass({
                          ...editingClass,
                          cr: e.target.value
                        })}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                      >
                        <option value="">Select CR (Optional)</option>
                        {users
                          ?.filter(user => 
                            user.role === 'cr' && 
                            (!user.crDetails?.assignedClass || 
                              user.crDetails.assignedClass === editingClass?._id ||
                              user._id === editingClass?.cr?._id ||
                              user._id === editingClass?.cr
                            )
                          )
                          ?.map(cr => (
                            <option key={cr._id} value={cr._id}>
                              {cr.name}
                            </option>
                          ))}
                      </select>
                    </div>

                    <div>
                      <label className="block text-sm font-medium text-gray-700">Teachers</label>
                      <select
                        multiple
                        value={editingClass?.teachers?.map(t => typeof t === 'object' ? t._id : t) || []}
                        onChange={(e) => setEditingClass({
                          ...editingClass,
                          teachers: Array.from(e.target.selectedOptions, option => option.value)
                        })}
                        className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm min-h-[100px]"
                      >
                        {users
                          ?.filter(user => user.role === 'teacher')
                          ?.map(teacher => (
                            <option key={teacher._id} value={teacher._id}>
                              {teacher.name}
                            </option>
                          ))}
                      </select>
                      <p className="mt-1 text-sm text-gray-500">Hold Ctrl/Cmd to select multiple teachers</p>
                    </div>
                  </div>
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => {
                      setEditMode({ type: null, data: null });
                      setEditingClass(null);
                    }}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 rounded-md"
                  >
                    Save Changes
                  </button>
                </div>
              </form>
            )}

            {editMode.type === 'user' && (
              <form onSubmit={handleEditUser} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Name</label>
                  <input
                    type="text"
                    value={editingUser?.name || ''}
                    onChange={(e) => setEditMode({
                      ...editMode,
                      data: { ...editMode.data, name: e.target.value }
                    })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Email</label>
                  <input
                    type="email"
                    value={editingUser?.email || ''}
                    onChange={(e) => setEditMode({
                      ...editMode,
                      data: { ...editMode.data, email: e.target.value }
                    })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Role</label>
                  <select
                    value={editingUser?.role || ''}
                    onChange={(e) => setEditMode({
                      ...editMode,
                      data: { ...editMode.data, role: e.target.value }
                    })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                  >
                    <option value="teacher">Teacher</option>
                    <option value="cr">CR</option>
                    <option value="admin">Admin</option>
                  </select>
                </div>
                <div className="flex justify-end space-x-3">
                  <button
                    type="button"
                    onClick={() => setEditMode({ type: null, data: null })}
                    className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 hover:bg-gray-200 rounded-md"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="px-4 py-2 text-sm font-medium text-white bg-indigo-600 hover:bg-indigo-700 rounded-md"
                  >
                    Save Changes
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>
      </div>
    );
  };

  const renderUsersList = () => (
    <div className="bg-white shadow rounded-lg p-6">
      <h2 className="text-lg font-semibold mb-4">All Users</h2>
      <div className="overflow-x-auto">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Name</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Email</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Role</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Details</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase">Actions</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {users.map((user) => (
              <tr key={user._id}>
                <td className="px-6 py-4 whitespace-nowrap">{user.name}</td>
                <td className="px-6 py-4 whitespace-nowrap">{user.email}</td>
                <td className="px-6 py-4 whitespace-nowrap capitalize">{user.role}</td>
                <td className="px-6 py-4">
                  {user.role === 'student' && (
                    <div className="text-sm">
                      <p>Enrollment: {user.enrollmentNumber || user.studentDetails?.enrollmentNumber}</p>
                      {user.studentDetails?.class && (
                        <p>Class: {
                          typeof user.studentDetails.class === 'object' 
                            ? user.studentDetails.class.name 
                            : classes.find(c => c._id === user.studentDetails.class)?.name || 'Unknown'
                        }</p>
                      )}
                    </div>
                  )}
                  {user.role === 'cr' && (
                    <div className="text-sm">
                      {user.crDetails?.assignedClass && (
                        <p>Class: {
                          typeof user.crDetails.assignedClass === 'object'
                            ? user.crDetails.assignedClass.name
                            : classes.find(c => c._id === user.crDetails.assignedClass)?.name || 'Unknown'
                        }</p>
                      )}
                    </div>
                  )}
                  {user.role === 'teacher' && (
                    <div className="text-sm">
                      <p>Classes: {
                        classes
                          .filter(c => c.teachers.includes(user._id))
                          .map(c => c.name)
                          .join(', ') || 'None'
                      }</p>
                    </div>
                  )}
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <button
                    onClick={() => handleEditClick('user', user)}
                    className="text-indigo-600 hover:text-indigo-900 mr-4"
                  >
                    Edit
                  </button>
                  <button
                    onClick={() => handleDeleteUser(user._id)}
                    className="text-red-600 hover:text-red-900"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <DashboardLayout>
      <div className="space-y-6">
        {/* Tab Navigation */}
        <div className="border-b border-gray-200">
          <nav className="-mb-px flex space-x-8">
            {['users', 'classes', 'updates'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`${
                  activeTab === tab
                    ? 'border-indigo-500 text-indigo-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                } whitespace-nowrap py-4 px-1 border-b-2 font-medium text-sm capitalize`}
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>

        {/* Users Tab */}
        {activeTab === 'users' && (
          <div className="space-y-6">
            {renderUserForm()}
            {renderUsersList()}
          </div>
        )}

        {/* Classes Tab */}
        {activeTab === 'classes' && (
          <div className="space-y-6">
            {renderClassForm()}
            {renderClassesList()}
          </div>
        )}

        {/* Updates Tab */}
        {activeTab === 'updates' && (
          <div className="space-y-6">
            {/* Create Update Form */}
            <div className="bg-white shadow rounded-lg p-6">
              <h2 className="text-lg font-semibold mb-4">Create College-Wide Update</h2>
              <form onSubmit={handleCreateUpdate} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Title</label>
                  <input
                    type="text"
                    value={newUpdate.title}
                    onChange={(e) => setNewUpdate({ ...newUpdate, title: e.target.value })}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    required
                  />
                </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700">Content</label>
                  <textarea
                    value={newUpdate.content}
                    onChange={(e) => setNewUpdate({ ...newUpdate, content: e.target.value })}
                    rows={4}
                    className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-indigo-500 focus:ring-indigo-500 sm:text-sm"
                    required
                  />
                </div>
                <button
                  type="submit"
                  className="inline-flex justify-center py-2 px-4 border border-transparent shadow-sm text-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                >
                  Post Update
                </button>
              </form>
            </div>

            {/* Updates List */}
            <div className="bg-white shadow rounded-lg p-6">
              <h2 className="text-lg font-semibold mb-4">All Updates</h2>
              <div className="space-y-4">
                {updates.map((update) => (
                  <div key={update._id} className="border-b pb-4">
                    <h3 className="font-medium">{update.title}</h3>
                    <p className="text-gray-600 mt-1">{update.content}</p>
                    <div className="mt-2 flex justify-between items-center">
                      <span className="text-sm text-gray-500">
                        {new Date(update.timestamp).toLocaleDateString()}
                      </span>
                      <div>
                        <button
                          onClick={() => {/* Handle edit */}}
                          className="text-indigo-600 hover:text-indigo-900 mr-4"
                        >
                          Edit
                        </button>
                        <button
                          onClick={() => handleDeleteUpdate(update._id)}
                          className="text-red-600 hover:text-red-900"
                        >
                          Delete
                        </button>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        )}
      </div>
      {editMode.type && renderEditModal()}
      {showStudentModal && renderStudentManagementModal()}
    </DashboardLayout>
  );
}
